import { useState } from "react";

interface Props {
    names: string[];
    heading: string;

    onSelectItem: (item: string) => void;

}

function ListGroup({names, heading, onSelectItem}: Props) {
    const name = 'Samantha Thompson';
    const username = 'tsammie16';
    const email = 'tsammie16@gmail.com';
    const phoneNum = '(636)357-9633';

    const [selectedIndex, setSelectedIndex] = useState(-1)


    return (
    <>
        <ul>
            <li>Name: {name}</li>
            <li>Username: {username}</li>
            <li>Email: {email}</li>

            <li>Phone Number: {phoneNum}</li>
            <h1>{heading}</h1>
            {names.map((name, index) => (
                <li
                className={selectedIndex === index ? 'bg-blue-600' : 'bg-blue-400'}
                key={name}
                onClick={() => {
                    setSelectedIndex(index)
                    onSelectItem(name)
                }}>
                    {name}</li>
            ))}
        </ul>
    </>
    )
}

export default ListGroup;
